const htmlmin = require('html-minifier')
const lazyloadimages = require('./plugins/lazy-load-images/.eleventy.js')
const imagealttagcheck = require('./plugins/img-alt-tag-check/.eleventy.js')
const ErrorOverlay = require('eleventy-plugin-error-overlay')

const now = String(Date.now())

module.exports = function (eleventyConfig) {
    /**
     * Setup nice overlay so we are foreced to fix errors
     */
    eleventyConfig.setBrowserSyncConfig({
        ...eleventyConfig.browserSyncConfig,
    })
    eleventyConfig.addPlugin(ErrorOverlay)

    /**
     * Watch styles generated by tailwind
     */
    // eleventyConfig.addWatchTarget('./tmp/style.css')

    eleventyConfig.addWatchTarget('./src')

    /**
     * Pass directories & files to top level of dist
     */
    eleventyConfig.addPassthroughCopy({ public: '/' })
    // eleventyConfig.addPassthroughCopy({
    //     './tmp/style.css': './style.css',
    // })
    // eleventyConfig.addPassthroughCopy({
    //     './node_modules/alpinejs/dist/alpine.js': './alpine.js',
    // })

    /**
     * Lets us use version in our site
     */
    eleventyConfig.addShortcode('version', function () {
        return now
    })

    /**
     * Validates that all images have an alt tag & value
     */
    eleventyConfig.addPlugin(imagealttagcheck)

    /**
     * Add native lazy loading to images
     */
    eleventyConfig.addPlugin(lazyloadimages)

    /**
     * Minify HTML
     */
    eleventyConfig.addTransform('htmlmin', (content, outputPath) => {
        if (outputPath.endsWith('.html')) {
            return htmlmin.minify(content, {
                useShortDoctype: true,
                removeComments: true,
                collapseWhitespace: true,
            })
        } else {
            return content
        }
    })

    return {
        passthroughFileCopy: true,
        dir: {
            input: 'src/pages',
            includes: '../_includes',
            layouts: '../_includes',
            data: '../_data',
            output: 'dist',
        },
    }
}
